import React from 'react'
import { Menu, Icon } from 'antd'
import styled from 'styled-components'
import { Link } from 'react-router-dom'

const StyledMenu = styled.div`
  width: 100%;
  display: flex;
  justify-content: center;
  align-items: center;
`

const StyledApp = styled.div``

export default ({ children }) => {
  const menuEntries = [
    {
      title: 'Search',
      href: '/search',
    },
  ]

  return (
    <StyledApp>
      <StyledMenu>
        {menuEntries.map(({ title, href }) => (
          <Menu>
            <Link to={href}>
              <Menu.Item key="mail">
                <Icon type="mail" />
                {title}
              </Menu.Item>
            </Link>
          </Menu>
        ))}
      </StyledMenu>
      {children}
    </StyledApp>
  )
}
